        private void ButtonBase_OnClick(object sender, RoutedEventArgs e)
        {

            WriteResx(@".\en-us.resx", Controls_Eng);
            WriteResx(@".\es-es.resx", Controls_Sp);

            void WriteResx(string resxName, Dictionary<string, object> dict )
            {
                if (resxName == null || dict == null) return;
                
                using (var resx = new ResXResourceWriter(resxName))
                {
                    foreach (var text in dict)
                    {
                        resx.AddResource(text.Key, text.Value);
                    }
                }
            }

            
            
        }
